export const projects = [
    {
      title: "UniMap",
      description:
        "Django based web application to help UVA students determine how busy locations across campus are in real-time using a voting system, with each vote remaining active for 3 hours. ",
      image: "projPics/unimap5.png",
      link: "https://github.com/maseelshah22/UniMap",
    },

    {
        title: "Fantasy Football Project",
        description:"Program to scrape NFL data from internet and export to an Excel file for data analysis. Can help users make informed decisions for fantasy football. Side Note - I WON the league the FIRST year I used this!",
