import React, { useState } from 'react'; // Import React and useState hook from React
// import { Link } from 'react-router-dom'; // Import Link component from react-router-dom
import './Navbar.css'; // Import styles for Navbar

const Navbar = () => {
  // State to manage mobile menu visibility
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);

  // Function to toggle mobile menu visibility
  const toggleMobileMenu = () => {
    setIsMobileMenuOpen(!isMobileMenuOpen);
  };

  return (
    <nav className="main-navbar"> {/* Main navigation container */}
      <div className="main-navbar-brand">
        {/* Logo or brand */}
        <Link to="/">MaseelShah</Link> {/* Link to home */}
      </div>
      <div className={`main-navbar-menu ${isMobileMenuOpen ? 'is-active' : ''}`}>
        {/* Menu items */}
        <ul>
        <div className='desktopMenu'>
        <Link activeClass='active'to='intro'spy={true} smooth={true} offset={-100} duration={500} className='desktopMenuListItem'>
          Home
        </Link>
        <Link activeClass='active' to='experience' spy={true} smooth={true} offset={-50} duration={500} className='desktopMenuListItem'>
          About
        </Link>
        <Link activeClass='active' to='works' spy={true}  smooth={true} offset={-50} duration={500} className='desktopMenuListItem'>
          Portfolio
        </Link>
        <Link activeClass='active' to='contactPage' spy={true} smooth={true} offset={500} duration={500} className='desktopMenuListItem'>
          Contact Me
        </Link>

      </div>
        </ul>
        {isMobileMenuOpen && <div className="close-button" onClick={toggleMobileMenu}>&times;</div>}
        {/* Close button for mobile menu */}
      </div>
      <div className="main-navbar-burger" onClick={toggleMobileMenu}>
        {/* Burger icon for mobile menu toggle */}
        <span></span>
        <span></span>
        <span></span>
      </div>
    </nav>
  );
};

export default Navbar;
